import { Badge, Card, CardActions, CardContent, CardHeader, CardMedia, Collapse, IconButton, IconButtonProps, Tooltip, Typography, styled } from "@mui/material";
import { Track } from "../types/Track";
import TrackImage from "./TrackImage";
import FavoriteIcon from '@mui/icons-material/Favorite';
import RemoveRedEyeIcon from '@mui/icons-material/RemoveRedEye';
import { getUser, getUserByToken, updateUser } from "../services/user";
import { trackForMutations } from "@reduxjs/toolkit/dist/immutableStateInvariantMiddleware";
import { useDispatch, useSelector } from "react-redux";
import { selectTrack } from "../redux/track/track.selector";
import { updateTrack } from "../redux/track/track.slice";
import { User } from "../types/user.types";
import { addFavorite, deleteFavorite } from "../services/user";
import ExpandMoreIcon from '@mui/icons-material/ExpandMore';
// import { ExpandMore } from "@mui/icons-material";
import { useState } from "react";
import { getCompanyById, getDayPartyById, getLevelById, getViewById } from "../utils/Mapper";
import getTrackLength from "../utils/generalFunctions.utils";
import TrackStepper from "./TrackStepper";
import EditIcon from '@mui/icons-material/Edit';
import CommentIcon from '@mui/icons-material/Comment';
import CommentsDrawer from "./CommentsDrawer";
import { useNavigate, useNavigation } from "react-router-dom";
import React from "react";
import DeleteIcon from '@mui/icons-material/Delete';

const ExpandMore = styled((props: any/*ExpandMoreProps*/) => {
  const { expand, ...other } = props;
  return <IconButton {...other} />;
})(({ theme, expand }) => ({
  transform: !expand ? 'rotate(0deg)' : 'rotate(180deg)',
  marginLeft: 'auto',
  transition: theme.transitions.create('transform', {
    duration: theme.transitions.duration.shortest,
  }),
}));

export default function CardTrack(props:{track:Track,hasToken:boolean, myTrack:boolean}){
  const [expanded, setExpanded] = useState(false);
const navigate=useNavigate();
  const handleExpandClick = () => {
    setExpanded(!expanded);
  };
  const dispatch=useDispatch()
  const tracks=useSelector(selectTrack)
  // console.log(tracks.filter(value=>{return value.id===props.track.id}))
  const handleFavoriteClick=async()=>{
    
    console.log("in put")
    let token=localStorage.getItem('user')
    if(token!==null&&token!==undefined&&token!==''){
      try{
        let res=await getUserByToken(token)
        if(res.status===401){
          alert("please login for more options")
        }
      let userById=await (res).json() as User;
      let user= (await getUser(userById.id)) ;
      console.log(user)
      if(user.favourites===undefined||user.favourites===null){
        user.favourites=[] as Track[];
      }
      console.log(user.favourites.filter((value:Track)=>{
        return value.id===props.track.id;
      }).length)
      if(user.favourites.filter((value:Track)=>{
        return value.id===props.track.id;
      }).length===0){
        console.log("add favorite")
      let track={...props.track}
      track.favourites+=1
      // user.favourites!.push(props.track)
      // props.track.favourites+=1;
      console.log(props.track)
      await addFavorite(user.id, props.track, token)
      
        
      dispatch(updateTrack(track))
    }else{
      let track={...props.track}
      track.favourites-=1
      // user.favourites!.filter(props.track)
      // props.track.favourites+=1;
      console.log(props.track)
      await deleteFavorite(user.id, props.track, token)
      
        
      dispatch(updateTrack(track))
      console.log("delete favorite")
    }
    }catch(error){
      console.error(error)
    }
    }else{
      alert('please login for more actions')
    }
  }
  const handleClickEdit=()=>{
    navigate(`/user/myTracks/updateTrack/${props.track.id}`)
  }
  const handleDelete=()=>[
    
  ]
return <>
<Card sx={{ }}>
      <CardHeader       
        title={props.track.title}       
      />
      <CardMedia>
        <TrackImage id={props.track.id}/>
      </CardMedia>
      <CardContent>
        <Typography variant="body2" color="text.secondary">
          {props.track.description}
        </Typography>
      </CardContent>
      <CardActions disableSpacing>
        
          {props.hasToken&&<IconButton aria-label="add to favorites" onClick={handleFavoriteClick}>
            <Badge badgeContent={props.track.favourites} color="primary"><FavoriteIcon /></Badge>
          </IconButton>}
        {props.myTrack&&<IconButton aria-label="edit" onClick={handleClickEdit}>
        <EditIcon/>
        </IconButton>}
        {props.hasToken&&<CommentsDrawer trackId={props.track.id}/>}
        {/* <IconButton aria-label="view">
          <RemoveRedEyeIcon/>
        </IconButton> */}
        {props.myTrack&&<Tooltip title="Delete">
        <IconButton>
          <DeleteIcon />
        </IconButton>
      </Tooltip>}
        <ExpandMore
          expand={expanded}
          onClick={handleExpandClick}
          aria-expanded={expanded}
          aria-label="show more"
        >
          <ExpandMoreIcon />
          </ExpandMore>
          
      </CardActions>
      <Collapse in={expanded} timeout="auto" >
        <CardContent>
          <Typography paragraph>Track Details:</Typography>
          <Typography paragraph>
            {props.track.description}
          </Typography>
          <Typography >
            level:{getLevelById(props.track.levelId)},{'\n'}
            suitable for: {getCompanyById( props.track.companyForTripId)},{'\n'}
            you are expected to see on that trip:{getViewById(props.track.viewId)},{'\n'}
            the best time for that track: {getDayPartyById(props.track.dayPartId)},{'\n'}
            this track takes about:{getTrackLength(props.track.length)}
          </Typography>
          
        </CardContent>
        <TrackStepper track={props.track}/>
      </Collapse>
      </Card>
</>
}
