import React, { useState, useEffect } from 'react';
import {getUsers} from '../services/user';
import Avatar from '@mui/material/Avatar';
import axios from '../utils/axios.util';
interface PictureProps {
  id: number;
}

const Picture: React.FC<PictureProps> = ({ id }) => {
  const [pictureUrl, setPictureUrl] = useState<string | null>(null);

  useEffect(() => {
    const fetchPicture = async () => {
        if(id!==0){
      try {
        const response = await fetch(`https://localhost:7090/api/User/${id}/picture`);
        if (response.ok) {
          const blob = await response.blob();
          const url = URL.createObjectURL(blob);
          setPictureUrl(url);
        } else {
          console.error('Failed to fetch picture');
        }
      } catch (error) {
        console.error('Error fetching picture:', error);
      }
    }
    };

    fetchPicture();

    // Clean up URL object when component unmounts
    return () => {
      if (pictureUrl) {
        URL.revokeObjectURL(pictureUrl);
      }
    };
  }, [id]);

  if (!pictureUrl) {
    return <div>Loading...</div>;
  }

  return <>
  {/* <img src={pictureUrl} alt="mdo" width="32" height="32" className="rounded-circle"/> */}
  <Avatar alt="Remy Sharp" src={pictureUrl} sx={{objectFit:'cover'}}/></>;
};

export default Picture;
