import axios from '../utils/axios.util'
import { Comment} from "../types/Comment"

export const getComments = async (token:string) => {
    const response = await fetch('https://localhost:7090/api/Comment',{headers:{"Authorization": `Bearer ${token}`}})
    // לקריאת get & delete אין body
    // const response = await fetch('https://jsonplaceholder.typicode.com/todos', { method: 'POST', body: JSON.stringify({}) })
    const comments = await response.json()
    
    return comments
}

export const getCommentsByTrackId = async (trackId:number,token:string) => {
    console.log("comment")
    const response = await fetch(`https://localhost:7090/api/Comment/ByTrackId/${trackId}`,{headers:{"Authorization": `Bearer ${token}`}})
    // לקריאת get & delete אין body
    // const response = await fetch('https://jsonplaceholder.typicode.com/todos', { method: 'POST', body: JSON.stringify({}) })
    const comments = await response.json()
    
    return comments
}


export const addComment= async (comment: Omit<Comment, 'id'>,token:string) => {
    console.log(comment)
    const response = await axios.post('/Comment', comment,{headers:{"Authorization":`Bearer ${token}`}})
    const newComment = response.data
    return newComment
}

export const updateComment = async (comment: Comment,token:string) => {
    const response = await axios.put(`/Comment/${comment.id}`, comment,{headers:{"Authorization":`Bearer ${token}`}})
    const updatedComment = response.data
    return updatedComment
}

export const deleteComment = async (id: number,token:string) => {
    const response = await axios.delete(`/Comment/${id}`,{headers:{"Authorization":`Bearer ${token}`}})
    return response
}
