import axios from '../utils/axios.util'
import { Track} from "../types/Track"

export const getTracks = async () => {
    const response = await fetch('https://localhost:7127/api/Track')
    // לקריאת get & delete אין body
    // const response = await fetch('https://jsonplaceholder.typicode.com/todos', { method: 'POST', body: JSON.stringify({}) })
    const tracks = await response.json()
    
    return tracks
}

export const addTrack= async (track: Omit<Track, 'id'>,token:string) => {
    console.log(stop)
    const response = await axios.post('/Track', track)
    const newTrack = response.data
    return newTrack
}

export const updateTrack = async (track: Track) => {
    const response = await axios.put(`/Track/${track.id}`, track,{headers:{"Authorization": `Bearer ${token}`}})
    const updatedTrack = response.data
    return updatedTrack
}

export const deleteTrack = async (id: number) => {
    const response = await axios.delete(`/Track/${id}`)
    return response
}
