import * as React from 'react';
import { useTheme } from '@mui/material/styles';
import Box from '@mui/material/Box';
import MobileStepper from '@mui/material/MobileStepper';
import Paper from '@mui/material/Paper';
import Typography from '@mui/material/Typography';
import Button from '@mui/material/Button';
import KeyboardArrowLeft from '@mui/icons-material/KeyboardArrowLeft';
import KeyboardArrowRight from '@mui/icons-material/KeyboardArrowRight';
import SwipeableViews from 'react-swipeable-views';
import { autoPlay } from 'react-swipeable-views-utils';

const AutoPlaySwipeableViews = autoPlay(SwipeableViews);

export default function Carousel(props: { images: any }) {
  const theme = useTheme();
  const [activeStep, setActiveStep] = React.useState(0);
  const [direction, setDirection] = React.useState(1); // 1 for forward, -1 for backward
  const maxSteps = props.images.length;

  const handleNext = () => {
    if (activeStep === maxSteps - 1) {
      setDirection(-1); // Reverse direction
    } else {
      setActiveStep((prevActiveStep) => prevActiveStep + 1);
    }
  };

  const handleBack = () => {
    if (activeStep === 0) {
      setDirection(1); // Forward direction
    } else {
      setActiveStep((prevActiveStep) => prevActiveStep - 1);
    }
  };

  const handleStepChange = (step: number) => {
    setActiveStep(step);
  };

  React.useEffect(() => {
    // Automatically reverse direction when reaching the boundaries
    if (direction === 1 && activeStep < maxSteps - 1) {
      setActiveStep((prev) => prev + 1);
    } else if (direction === -1 && activeStep > 0) {
      setActiveStep((prev) => prev - 1);
    }
  }, [direction, activeStep, maxSteps]);

  return (
    <Box sx={{ maxWidth: '100%', flexGrow: 1 }}>
      <AutoPlaySwipeableViews
        axis={theme.direction === 'rtl' ? 'x-reverse' : 'x'}
        index={activeStep}
        onChangeIndex={handleStepChange}
        enableMouseEvents
      >
        {props.images.map((step: any, index: number) => (
          <div key={step.label}>
            {Math.abs(activeStep - index) <= 2 ? (
              <Box sx={{ position: 'relative' }}>
                <Box
                  component="img"
                  sx={{
                    height: '500px',
                    display: 'block',
                    overflow: 'hidden',
                    width: '100%',
                  }}
                  src={step.img}
                  alt={step.label}
                />
                <Box
                  sx={{
                    position: 'absolute',
                    right: '5%',
                    bottom: '10%',
                    background: 'rgba(144, 238, 144, 0.7)',
                    padding: '10px',
                    borderRadius: '5px',
                    maxWidth: '80%',
                  }}
                >
                  <Typography
                    variant="h6"
                    sx={{
                      fontWeight: 'bold',
                      fontFamily: "'Roboto', sans-serif",
                      fontSize: '2rem',
                      letterSpacing: '1px',
                      textShadow: '1px 1px 2px rgba(0, 0, 0, 0.5)',
                    }}
                  >
                    {step.title}
                  </Typography>
                  <Typography
                    variant="body1"
                    sx={{
                      fontFamily: "'Roboto', sans-serif",
                      fontSize: '1.5rem',
                      letterSpacing: '0.5px',
                      textShadow: '1px 1px 2px rgba(0, 0, 0, 0.5)',
                    }}
                  >
                    {step.content}
                  </Typography>
                </Box>
              </Box>
            ) : null}
          </div>
        ))}
      </AutoPlaySwipeableViews>
      <MobileStepper
        steps={maxSteps}
        position="static"
        activeStep={activeStep}
        nextButton={
          <Button
            size="small"
            onClick={handleNext}
            sx={{ color: 'forestgreen' }}
          >
            Next
            {theme.direction === 'rtl' ? <KeyboardArrowLeft /> : <KeyboardArrowRight />}
          </Button>
        }
        backButton={
          <Button
            size="small"
            onClick={handleBack}
            sx={{ color: 'forestgreen' }}
          >
            {theme.direction === 'rtl' ? <KeyboardArrowRight /> : <KeyboardArrowLeft />}
            Back
          </Button>
        }
      />
    </Box>
  );
}
