import { useLoaderData } from "react-router-dom";
import MyFavorites from "../sections/MyFavorites.section";
import { useEffect, useState } from "react";
import { getUser, getUserByToken } from "../services/user";
import { User } from "../types/user.types";

export default function MyFavoritesPage(){
    const [user, setUser]=useState<User>()
    const[data,setData]=useState<any>()
    
    const [loadedUser,setLoadedUser]=useState(false)
    useEffect(()=>{
        loadUser();
    },[])
    useEffect(()=>{
        const loaded=useLoaderData()
        setData(data)
    },[])
    useEffect(()=>{
        loadUser();
        
    })
    useEffect(()=>{
        if(loadedUser){
            setData(user?.favourites)
            setLoadedUser(false)
        }
    },[loadedUser])
    async function loadUser(){
        const token=localStorage.getItem('user')
        let tempUser=await(await getUserByToken(token!)).json()
        let tempUser2=await getUser(tempUser.id)
        setUser(tempUser2)
        setLoadedUser(true)
    }
    return <MyFavorites tracks={data}/>
}
