import { useEffect, useState } from "react";
import TrackFind from "../sections/TrackFind.section";
import { Filter } from "../types/Filter.types";
import { useLoaderData } from "react-router-dom";
import { Track } from "../types/Track";
import { getTrackByArea, getTrackByText } from "../services/track";
import { getCompanyById, getDayPartyById, getLevelByDescription, getLevelById, getViewById } from "../utils/Mapper";

export default function TrackPage(){
    const [desc,setDesc]=useState<string|undefined>(undefined)
    const[filter,setFilter]=useState<Filter|undefined>(undefined)
    const data:any=useLoaderData()
    const[allTracks,setAllTracks]=useState<Track[]>([])
    const[lastArea,setLastArea]=useState("")
    const [tracks,setTracks]=useState<Track[]>([])
    useEffect(()=>{
        setTracks(data)
        setAllTracks(data)
    },[])
    useEffect(()=>{
        if(desc!==undefined){
            console.log("desc")
            console.log(desc)
            loadByDesc();
        }
    },[desc])
    useEffect(()=>{
        if(filter!==undefined){
            loadByFilter();
        }
    },[filter])
    async function loadByDesc(){
        let temp=await getTrackByText(desc!)
        setTracks([...temp])
        setDesc(undefined)
    }
    async function loadByFilter(){
        let temp=[...allTracks]
        if(lastArea===""||lastArea!==filter!.area){
         temp=await getTrackByArea(filter!.area)
        setLastArea(filter!.area)
        setAllTracks([...temp])
        }
        // setTracks([...temp])\
        console.log("temp")
        console.log(temp)
        let filtered=[...temp!.map((value:Track)=>{
             let flag=true;
            if(filter!==undefined&&filter!==null){
                if(filter.level!==undefined&&filter.level!==""&&getLevelById(value.levelId)!==filter.level){
                    flag=false;
                }
                if(filter.dayPart!==undefined&&filter.dayPart!==""&&getDayPartyById(value.dayPartId)!==filter.dayPart){
                    flag=false;
                }
                if(filter.company!==undefined&&filter.company!==""&&getCompanyById(value.companyForTripId)!==filter.company){
                    flag=false;
                }
                if(filter.view!==undefined&&filter.view!==""&&getViewById(value.viewId)!==filter.view){
                    flag= false;
                }
            }
            if(flag){}
            return value;
        })];
        console.log("filtered")
        console.log(filtered)
        setFilter(undefined)
        // console.log(tracks)
        setTracks([...filtered])
    }
    useEffect(()=>{
        console.log("tracks")
        console.log(tracks)
    },[tracks])
    return <>
    <TrackFind tracks={tracks} setDesc={setDesc} setFilter={setFilter}/>
    </>
}
