import { Badge, Card, CardActions, CardContent, CardHeader, CardMedia, IconButton, Typography } from "@mui/material";
import { Track } from "../types/Track";
import TrackImage from "./TrackImage";
import FavoriteIcon from '@mui/icons-material/Favorite';
import RemoveRedEyeIcon from '@mui/icons-material/RemoveRedEye';
import { getUser, getUserByToken, updateUser } from "../services/user";
import { trackForMutations } from "@reduxjs/toolkit/dist/immutableStateInvariantMiddleware";
import { useDispatch, useSelector } from "react-redux";
import { selectTrack } from "../redux/track/track.selector";
import { updateTrack } from "../redux/track/track.slice";
import { User } from "../types/user.types";
export default function CardTrack(props:{track:Track}){
  const dispatch=useDispatch()
  const tracks=useSelector(selectTrack)
  // console.log(tracks.filter(value=>{return value.id===props.track.id}))
  const handleFavoriteClick=async()=>{
    
    console.log("in put")
    let token=localStorage.getItem('user')
    if(token!==null&&token!==undefined&&token!==''){
      try{
      let userById=await (await getUserByToken(token)).json() as User;
      let user= (await getUser(userById.id)) ;
      console.log(user)
      if(user.favourites===undefined||user.favourites===null){
        user.favourites=[] as Track[];
      }

      user.favourites!.push(props.track)
      // props.track.favourites+=1;
      
      const formData=new FormData()
      
      try {
        console.log("hello3")
        let token=localStorage.getItem('user');
        console.log(token)
        const response = await fetch(`https://localhost:7090/api/User/${user.id}/picture`,{headers:{"Authorization": `Bearer ${token}`}});
        if (response) {
          console.log(response)
          const blob = await response.blob();
          const url = URL.createObjectURL(blob);
          formData.append('pictureFile', blob);
        } else {
          console.error('Failed to fetch picture');
        }
      } catch (error) {
        console.error('Error fetching picture:', error);
      }
        
        
        formData.append('experienceId', user.experienceId.toString());
        formData.append('statusId', user.statusId.toString());
        user.favourites?.forEach((track:Track, index:number) => {
          formData.append(`favourites[${index}].name`, track.title);
          formData.append(`favourites[${index}].duration`, track.duration.toString());
          // Append other track properties as needed
      });
        formData.append('adressX', user.adressX.toString());
        formData.append('adressY', user.adressY.toString());
        formData.append('birthDate', user.birthDate);
        formData.append('mail', user.mail);
        formData.append('profilePicture', user.profilePicture!);
        formData.append('tracks', user.tracks);
        formData.append('name', user.name);
        formData.append('id', user.id);
        formData.forEach((value,key)=>{
          console.log(`${key}: ${value}`)
        })
      await updateUser(user.id,formData,token)
      dispatch(updateTrack(props.track))
    }catch(error){
      console.error(error)
    }
    }else{
      alert('please login for more actions')
    }
  }
return <>
<Card sx={{ }}>
      <CardHeader       
        title={props.track.title}       
      />
      <CardMedia>
        <TrackImage id={props.track.id}/>
      </CardMedia>
      <CardContent>
        <Typography variant="body2" color="text.secondary">
          {props.track.description}
        </Typography>
      </CardContent>
      <CardActions disableSpacing>
        
          <IconButton aria-label="add to favorites" onClick={handleFavoriteClick}>
            <Badge badgeContent={props.track.favourites} color="primary"><FavoriteIcon /></Badge>
          </IconButton>
        
        <IconButton aria-label="view">
          <RemoveRedEyeIcon/>
        </IconButton>
        
      </CardActions>
      </Card>
</>
}
