import * as React from 'react';
import Avatar from '@mui/material/Avatar';
import Button from '@mui/material/Button';
import CssBaseline from '@mui/material/CssBaseline';
import TextField from '@mui/material/TextField';
import FormControlLabel from '@mui/material/FormControlLabel';
import Checkbox from '@mui/material/Checkbox';
import Link from '@mui/material/Link';
import Grid from '@mui/material/Grid';
import Box from '@mui/material/Box';
import axios from '../utils/axios.util';
import LockOutlinedIcon from '@mui/icons-material/LockOutlined';
import Typography from '@mui/material/Typography';
import Container from '@mui/material/Container';
import { createTheme, ThemeProvider } from '@mui/material/styles';
import { Autocomplete, AutocompleteChangeDetails, Input } from '@mui/material';
import { useDispatch, useSelector } from 'react-redux';
import { selectExperience } from '../redux/experience/experience.selector';
import { selectStatus } from '../redux/status/status.selector';
import { useEffect, useState } from 'react';
import Map from '../components/Map';
import exp from 'constants';
import { DoNotStepOutlined } from '@mui/icons-material';
import { User } from '../types/user.types';
import { getExperienceByDescription, getStatusByDescription } from '../utils/Mapper';
import { selectUser } from '../redux/user/user.selector';
import { addUser } from '../redux/user/user.thunk';
// function Copyright(props: any) {
//   return (
//     <Typography variant="body2" color="text.secondary" align="center" {...props}>
//       {'Copyright © '}
//       <Link color="inherit" href="https://mui.com/">
//         Your Website
//       </Link>{' '}
//       {new Date().getFullYear()}
//       {'.'}
//     </Typography>
//   );
// }

// TODO remove, this demo shouldn't need to reset the theme.
const defaultTheme = createTheme();

export default function SignUpForm() {
    const experiences=useSelector(selectExperience)
    const status=useSelector(selectStatus)
    const [address, setAddress]=useState("")
    const dispatch =useDispatch();
    const user=useSelector(selectUser)
    const [imageToShow, setImageToShow]=useState<string | undefined>(undefined)
    console.log(experiences)
    const [x, setX]=useState(0)
    const [y, setY]=useState(0)
    const [st,setSt]=useState<string>("single")
    const [ex,setEx]=useState<string>("junior")
    const [image, setImage]=useState();
    console.log(ex)
    console.log("hello")
    
  const handleSubmit = async(event: React.FormEvent<HTMLFormElement>) => {
    event.preventDefault();
    const data = new FormData(event.currentTarget);
    const dataToSend={
      mail: data.get('email'),
      profilePicture:imageToShow,
      experienceId:getExperienceByDescription(ex),
      statusId:getStatusByDescription( st),
      birthDate:data.get('birthDate'),
      name:data!.get('Name'),
      addressX:x,
      addressY:y
    }as Omit<User,'id'>;
    const formData = new FormData(event.currentTarget);
        
        formData.append('poictureFile', image!);
        formData.append('experienceId', getExperienceByDescription(ex).toString());
        formData.append('statusId', getStatusByDescription(st).toString());
       
        formData.append('addressX', x.toString());
        formData.append('addressY', y.toString());
    console.log(dataToSend)
    // addUser(formData)
    try{
      const response = await axios.post('/User', formData, {
        headers: {
          'Content-Type': 'multipart/form-data'
        }
      });
      
      console.log('Data sent successfully:', response.data);
    } catch (error) {
      console.error('Error sending data:', error);
    }

  };
  const handleFileChange = (event:any) => {
    const selectedImage = event.target.files[0];
    setImage(selectedImage);
    setImageToShow(URL.createObjectURL(selectedImage))
  };

useEffect(()=>{
    
    console.log("hello")
});
// Listen for the 'change' event


  return (
    <ThemeProvider theme={defaultTheme}>
      <Container component="main" maxWidth="xs">
        <CssBaseline />
        <Box
          sx={{
            marginTop: 8,
            display: 'flex',
            flexDirection: 'column',
            alignItems: 'center',
          }}
        >
          {/* <Avatar sx={{ m: 1, bgcolor: 'secondary.main' }}>
            <LockOutlinedIcon />
          </Avatar> */}
          {image && <Avatar alt="Uploaded" src={imageToShow} style={{ width: 150, height: 150 }} />}
          <Grid item xs={12}>
                <input
                  accept="image/*"
                  id="contained-button-file"
                  multiple
                  type="file"
                  onChange={handleFileChange}
                  style={{ display: 'none' }}
                />
                <label htmlFor="contained-button-file">
                  <Button variant="contained" color="primary" component="span">
                    Upload Image
                  </Button>
                </label>
              </Grid>
          <Typography component="h1" variant="h5">
            Sign up
          </Typography>
          <Box component="form" noValidate onSubmit={handleSubmit} sx={{ mt: 3 }}>
            <Grid container spacing={2}>
            <Grid item xs={12}>
          {/* Display the selected image as Avatar */}
          
        </Grid>
              <Grid item xs={12} >
                <TextField
                  autoComplete="given-name"
                  name="Name"
                  required
                  fullWidth
                  id="Name"
                  label="Name"
                  autoFocus
                />
              </Grid>
              
              <Grid item xs={12} >
                <Autocomplete
                disablePortal
                id="experience"
                // inputValue={experiences}
                inputValue={ex}
                onInputChange={(event, newInputValue) => {
                  setEx(newInputValue);
                  console.log(newInputValue)
                }}
                        // onClick={onClick}
                options={experiences}
                sx={{ width: 300 }}
                renderInput={(params) => <TextField {...params} label="Experience" />}
                />
              </Grid>
              <Grid item xs={12} >
              <Autocomplete
                disablePortal
                id="status"
                options={status}
                sx={{ width: 300 }}
                inputValue={st}
                onInputChange={(event, newInputValue) => {
                  setSt(newInputValue);
                  console.log(newInputValue)
                }}   
                renderInput={(params) => <TextField {...params} label="Status" />}
                />
              </Grid>
              <Grid item xs={12}>
                <TextField
                  required
                  fullWidth
                  id="email"
                  label="Email Address"
                  name="mail"
                  autoComplete="email"
                />
              </Grid>
              <Grid item xs={12}>
                <TextField
                  required
                  fullWidth
                  id="birthDate"
                  label="Birth Date"
                  name="birthDate"
                  autoComplete="birthDate"
                  type="date"
                />
              </Grid>
              <Grid item xs={12}>
                <Map setAddress={setAddress} setX={setX} setY={setY}/>
              </Grid>
              <Grid item xs={12}>
                <TextField
                  required
                  fullWidth
                  name="address"
                  label="Address"
                  type="address"
                  id="address"
                  autoComplete="new-address"
                  value={address}
                />
              </Grid>
              <Grid item xs={12}>
                <FormControlLabel 
                  control={<Checkbox value="allowExtraEmails" color="primary" />}
                  label="I want to receive inspiration, marketing promotions and updates via email."
                />
              </Grid>
            </Grid>
            <Button
              type="submit"
              fullWidth
              variant="contained"
              
              sx={{ mt: 3, mb: 2 }}
            >
              Sign Up
            </Button>
            <Grid container justifyContent="flex-end">
              <Grid item>
                <Link href="#" variant="body2">
                  Already have an account? Sign in
                </Link>
              </Grid>
            </Grid>
          </Box>
        </Box>
        {/* <Copyright sx={{ mt: 5 }} /> */}
      </Container>
    </ThemeProvider>
  );
}
