import React, { useEffect, useState } from 'react';
import { GoogleMap, DirectionsService, DirectionsRenderer, Marker } from '@react-google-maps/api';

interface Coordinate {
  lat: number;
  lng: number;
}

interface Stop {
  x: number;
  y: number;
}

interface Track {
  startX: number;
  startY: number;
  endX: number;
  endY: number;
  stops: Stop[];
}

interface MapProps {
  track: Track;
  apiKey: string;
}

const MapComponent: React.FC<MapProps> = ({ track, apiKey }) => {
  const [directions, setDirections] = useState<google.maps.DirectionsResult | null>(null);

  useEffect(() => {
    const script = document.createElement('script');
    script.src = `https://maps.googleapis.com/maps/api/js?key=${apiKey}`;
    script.async = true;
    script.onload = () => {
      // Once the Google Maps API is loaded, proceed to fetch directions
      fetchDirections();
    };
    document.body.appendChild(script);

    return () => {
      document.body.removeChild(script);
    };
  }, [apiKey]);

  const fetchDirections = () => {
    const directionsService = new google.maps.DirectionsService();

    const waypoints = track.stops.map(stop => ({
      location: new google.maps.LatLng(stop.x, stop.y),
      stopover: true,
    }));

    const origin = new google.maps.LatLng(track.startX, track.startY);
    const destination = new google.maps.LatLng(track.endX, track.endY);

    directionsService.route(
      {
        origin: origin,
        destination: destination,
        waypoints: waypoints,
        travelMode: google.maps.TravelMode.DRIVING,
      },
      (result, status) => {
        if (status === google.maps.DirectionsStatus.OK) {
          setDirections(result);
        } else {
          console.error(`Directions service failed due to ${status}`);
        }
      }
    );
  };

  return (
    <div style={{ height: '400px', width: '100%' }}>
      <GoogleMap center={{ lat: track.startX, lng: track.startY }} zoom={10} mapContainerStyle={{ height: '100%', width: '100%' }}>
        {directions && <DirectionsRenderer directions={directions} />}
        {/* Marker for the start point */}
        <Marker position={{ lat: track.startX, lng: track.startY }} label="S" />
        {/* Marker for the end point */}
        <Marker position={{ lat: track.endX, lng: track.endY }} label="E" />
        {/* Markers for stops */}
        {track.stops.map((stop, index) => (
          <Marker key={index} position={{ lat: stop.x, lng: stop.y }} label={`${index + 1}`} />
        ))}
      </GoogleMap>
    </div>
  );
};

export default MapComponent;
