import { InternalAxiosRequestConfig, AxiosResponse } from "axios";
import { getSession, isValidToken, removeSession } from "../auth/auth.utils"
import { ENDPOINTS, baseUrl } from "../api/endpoints";

export const authRequestMiddleware = async (request: InternalAxiosRequestConfig) => {
    if (request.url !== ENDPOINTS.userByToken) {
        const user = await getSession()
        if (request.url !== ENDPOINTS.addUser && request.url?.substring(0, request.url?.lastIndexOf("/") + 1) !== ENDPOINTS.getTracksByArea && request.url?.substring(0, request.url?.lastIndexOf("/") + 1) !== ENDPOINTS.getTracksByText && request.url !== ENDPOINTS.getAllTracks) {
            if (!user || !isValidToken(user.token)) {
                removeSession();
                Promise.reject('Unauthorized')
            }
        }
    }
    return request
}

export const authResponseMiddleware = (response: AxiosResponse) => {
    if (response.status === 401) {
        alert("response")
        removeSession();
        Promise.reject('Unauthorized')
    }
    return response
}
