import axios from '../utils/axios'
import { Track} from "../types/Track"
import { ENDPOINTS } from '../api/endpoints'

export const getTracks = async () => {
    const response = await axios.get(ENDPOINTS)
    const tracks = response.data
    return tracks
}
export const getTrackById = async (id:number) => {
    const response = await fetch(`https://localhost:7090/api/Track/${id}`)
    const tracks = await response.json()
    return tracks
}
export const getTrackByArea = async (area:string) => {
    const response = await axios.get(`/Track/TracksByArea/${area}`)
    const tracks =  response.data
    return tracks
}
export const getMyTracks=async(id:number,token:string)=>{
    const response=await fetch(`https://localhost:7090/api/Track/MyTracks/${id}`, { headers: { "Authorization": `Bearer ${token}` } });
    return response;
}
export const getTrackByText = async (text:string) => {
    const response = await axios.get(`/Track/TracksByText/${text}`)
    const tracks =  response.data
    return tracks
}
export const addTrack= async (track: FormData,token:string) => {
    const response = await axios.post('/Track', track,{headers:{"Authorization": `Bearer ${token}`}})
    return response
}

export const updateTrack = async (id:number,track: FormData, token:string) => {
    const response = await axios.put(`/Track/${id}`, track,{headers:{"Authorization": `Bearer ${token}`}})
    const updatedTrack = response.data
    return updatedTrack
}

export const deleteTrack = async (id: number, token:string) => {
    const response = await axios.delete(`/Track/${id}`,{headers:{"Authorization": `Bearer ${token}`}})
    return response
}
