import { Avatar, Box, Grid, IconButton, Pagination, Stack, Typography, createSvgIcon } from "@mui/material"
import { grey } from "@mui/material/colors"
import { useEffect, useState } from "react"
import { Track } from "../types/Track";
import CardTrack from "../components/CardTrack";
import { useDispatch, useSelector } from "react-redux";
import { selectTrack } from "../redux/track/track.selector";
import { getAllTracks } from "../redux/track/track.slice";
import { useNavigate } from "react-router-dom";
const PlusIcon = createSvgIcon(
  // credit: plus icon from https://heroicons.com/
  <svg
    xmlns="http://www.w3.org/2000/svg"
    fill="none"
    viewBox="0 0 24 24"
    strokeWidth={1.5}
    stroke="currentColor"
  >
    <path strokeLinecap="round" strokeLinejoin="round" d="M12 4.5v15m7.5-7.5h-15" />
  </svg>,
  'Plus',
);
export default function MyTracks(){
  const tracks =useSelector(selectTrack)
  const dispatch=useDispatch()
  const [uploaded, setUploaded]=useState<boolean>(false)
  const navigate=useNavigate()
  
    const[token,setToken]= useState<string|null>("")
    loadTracks();
    useEffect(()=>{
        setToken(localStorage.getItem('user'))
        console.log("hi")
        
    },[])
    async function loadTracks(){
      if(!uploaded){
      let token=localStorage.getItem('user')
        if(token!==undefined&&token!==null&&token!==""){
            try {
                console.log("hello4")
                
                console.log(token)
                const response = await fetch(`https://localhost:7090/api/User/UserByToken`,{headers:{"Authorization": `Bearer ${token}`}});
                if (response) {
                    let id=(await response.json()).id
                  console.log(id)
                  try {
                    // console.log("hello3")
                    // let token=localStorage.getItem('user');
                    // console.log(token)
                    const response2 = await fetch(`https://localhost:7090/api/Track/MyTracks/${id}`,{headers:{"Authorization": `Bearer ${token}`}});
                    if (response2) {
                        let res=await response2.json()
                      console.log(res)
                      dispatch(getAllTracks(res))
                      
                    } else {
                      console.error('Failed to fetch picture');
                    }
                  } catch (error) {
                    console.error('Error fetching picture:', error);
                  }
                } else {
                  console.error('Failed to fetch picture');
                }
              } catch (error) {
                console.error('Error fetching picture:', error);
              }

            

        }
        setUploaded(!uploaded)
      }
    }
    useEffect(()=>{
      console.log(tracks);
    },[tracks])
    function handleClick(){
      console.log("click")
      navigate('user/myTracks/addTrack')
    }

    return <><Grid container gap={2} alignItems={"center"} justifyItems={"center"} alignContent={"center"} justifyContent={"center"}>
    <Box component="div" alignItems={"center"} alignContent={"center"}>
      <Avatar sx={{ bgcolor: grey[500] ,width:"600px", height:"300px",margin:'50px', marginLeft:"150px"}} variant="rounded">
        <IconButton onClick={()=>handleClick}><PlusIcon fontSize="large" titleAccess="add track" onClick={handleClick}/></IconButton>
      </Avatar>
    </Box>
    <Grid container gap={2}>
      {tracks&&tracks.map((track)=>{
        return(<Grid xs={12} lg={3} key={track.id}>
          <CardTrack track={track}/>
        </Grid>);
      })}

    </Grid></Grid>
    </>
}
