import { createSlice } from '@reduxjs/toolkit'
import {getUsers} from '../../services/user'
import { User } from '../../types/user.types'

const userSlice = createSlice({
    name: 'user',
    initialState: {id:0} as User|undefined,
    reducers: {
        addUser: (state: User|undefined, action) => {
            return action.payload
        },
        deleteUser: (state: User|undefined) => {
            return {
                id:0,
                name:'',
                birthDate:'',
                mail:'',
                addressX:0,
                addressY:0,
                statusId:1,
                experienceId:1,
                profilePicture:'',
                pictureFile:undefined,
                myTracks:[],
                Favourites:[]
            }
        },
        updateUser:(state:User|undefined,action)=>{
            if(state){
                state.Favourites=action.payload.Favourites;
                state.addressX=action.payload.addressX;
                state.addressY=action.payload.addressY
                state.statusId=action.payload.statusId
                state.experienceId=action.payload.experienceId
                state.birthDate=action.payload.birthDate
                state.name=action.payload.name
                state.mail=action.payload.mail
                state.myTracks=action.payload.myTracks
                state.pictureFile=action.payload.pictureFile
                state.profilePicture=action.payload.profilePicture
            }
        }
    }
})
// async function InitialAllUsers(){
//     userSlice.actions.getAllUsers();
// }
export const {addUser,deleteUser,updateUser}=userSlice.actions
export default userSlice.reducer
