import axios from '../utils/axios.util'
import { Track} from "../types/Track"

export const getTracks = async () => {
    const response = await fetch('https://localhost:7127/api/Track')
    // לקריאת get & delete אין body
    // const response = await fetch('https://jsonplaceholder.typicode.com/todos', { method: 'POST', body: JSON.stringify({}) })
    const tracks = await response.json()
    
    return tracks
}
export const getTrackById = async (id:number) => {
    const response = await fetch(`https://localhost:7127/api/Track/${id}`)
    // לקריאת get & delete אין body
    // const response = await fetch('https://jsonplaceholder.typicode.com/todos', { method: 'POST', body: JSON.stringify({}) })
    const tracks = await response.json()
    
    return tracks
}
export const addTrack= async (track: FormData,token:string) => {
    // console.log(stop)
    const response = await axios.post('/Track', track,{headers:{"Authorization": `Bearer ${token}`}})
    const newTrack = response.data
    return newTrack
}

export const updateTrack = async (id:number,track: FormData, token:string) => {
    const response = await axios.put(`/Track/${id}`, track,{headers:{"Authorization": `Bearer ${token}`}})
    const updatedTrack = response.data
    return updatedTrack
}

export const deleteTrack = async (id: number, token:string) => {
    const response = await axios.delete(`/Track/${id}`,{headers:{"Authorization": `Bearer ${token}`}})
    return response
}
