import React from "react";
import Autocomplete from "@mui/material/Autocomplete";
import TextField from "@mui/material/TextField";

const MyAutocomplete = (props:{ onChange:(value:any)=>void, options:string[] }) => {
    
  const handleInputChange = (event:any, value:any) => {
    props.onChange(value); // Call the onChange function with the selected value
  };

  return (
    <Autocomplete
      disablePortal
      id="status"
      options={props.options}
      sx={{ width: 300 }}
      onChange={handleInputChange} // Pass the handleInputChange function to onChange prop
      renderInput={(params) => <TextField {...params} />}
    />
  );
};

export default MyAutocomplete;
