import React from 'react';
import { Navigate as BigNav, Navigate, createBrowserRouter, useNavigate } from "react-router-dom";
import Layout from "../layouts/Layout";
import HomePage from "../pages/HomePage";
import SignUpForm from '../sections/SignUpForm';
import SignUpPage from '../pages/SignUpPage';
import TrackPage from '../pages/TracksPage';
import MyTracks from '../sections/MyTracks';
import AddTrackPage from '../pages/AddTrackPage';
import MyTracksPage from '../pages/MyTracksPage';
import UpdateTrack from '../sections/UpdateTrack';
import { getMyTracks, getTrackByArea, getTrackById, getTracks } from '../services/track';
import UpdateUser from '../sections/UpdateUser';
import { getUser, getUserByToken } from '../services/user';
import MyFavoritesPage from '../pages/MyFavoritesPage';
import { Track } from '../types/Track';
import { getCompanyById, getViewById } from '../services/Mapper';
import { useSelector } from 'react-redux';
import { selectUser } from '../redux/user/user.selector';
import { backHome } from '../utils/backHome.util';
import AuthGuard from '../auth/AuthGuard';
import { isValidToken } from '../auth/auth.utils';
export const router = createBrowserRouter([
    {
        path: '/',
        element: <Layout />,
        children: [
            {
                path: '/',
                element: <HomePage />,
            },

            {
                path: 'user/',
                children: [

                    {
                        path: 'myTracks/',
                        element: <AuthGuard />,
                        children: [
                            {
                                path: '',
                                element: <MyTracksPage />,
                                loader: async () => {
                                    let token = localStorage.getItem('user')
                                    if (isValidToken(token)) {
                                        try {
                                            const response = await getUserByToken(token!)
                                            if (response) {
                                                if (response.status === 401) {
                                                    alert("you are not authorized")
                                                    return null;
                                                } else {
                                                    let id = (await response.json()).id
                                                    try {
                                                        const response2 = await getMyTracks(id, token!);
                                                        if (response2) {
                                                            let res = await response2.json()
                                                            return res;

                                                        } else {
                                                            console.error('Failed to fetch picture');
                                                        }
                                                    } catch (error) {
                                                        console.error('Error fetching picture:', error);
                                                    }
                                                }
                                            } else {
                                                console.error('Failed to fetch picture');
                                            }
                                        } catch (error) {
                                            console.error('Error fetching picture:', error);
                                        }


                                    } else {
                                        return null;
                                    }
                                }
                            },
                            {
                                path: 'updateTrack/:id',
                                element: <UpdateTrack />,
                                loader: async ({ params }) => {
                                    const track = await getTrackById(Number(params.id!))
                                    return track

                                },
                            },
                            {
                                path: 'addTrack',
                                element: <AddTrackPage />
                            }
                        ]
                    },
                    {
                        path: 'favorites',
                        element: <MyFavoritesPage />,

                    },
                    {
                        path: 'myProfile',
                        element: <UpdateUser />,
                        loader: async () => {
                            const token = localStorage.getItem('user')
                            let response = await getUserByToken(token!)
                            return response.user;
                        }
                    },
                    {
                        path: 'updateProfile'
                    }

                ]
            },
            {
                path: '/SignIn',
                element: <SignUpPage />
            },
            {
                path: '/findTrack',
                element: <TrackPage />,
                loader: async () => {
                    let tracks = await getTrackByArea("north");
                    return tracks
                }
            },
            {
                path: '/findTrack/:option',
                element: <TrackPage />,
                loader: async ({ params }) => {
                    let tracks = [] as Track[];

                    if (params.option !== "" && params.option !== undefined && params.option !== null) {
                        tracks = await getTracks();
                        tracks = tracks.filter((value) => {
                            if (params.option == "forest" && getViewById(value.viewId) === "forest") {
                                return true;
                            }
                            if (params.option == "sea" && getViewById(value.viewId) === "sea") {
                                return true;
                            }
                            if (params.option == "dessert" && getViewById(value.viewId) === "dessert") {
                                return true;
                            }
                            if (params.option === "market" && getViewById(value.viewId) === "market") {
                                return true;
                            }
                            if (params.option == "city" && getViewById(value.viewId) === "city") {
                                return true;
                            }
                            if (params.option == "family" && getCompanyById(value.companyForTripId) === "with family") {
                                return true;
                            }
                            return false;
                        });
                    } else {
                        tracks = await getTrackByArea("north");
                    }
                    return tracks;
                }
            },
            {
                path: '/viewTrack',

            }


        ]
    },

    {
        path: '*',
        element: <Navigate to="/" />,
    },

]);
