import { Track } from "../types/Track";
import { Stop } from "../types/Stop";
import { useDispatch, useSelector } from "react-redux";
import { selectLevel } from "../redux/level/level.selector";
import { selectDayPart } from "../redux/dayPart/dayPart.selector";
import { selectCompany } from "../redux/company/company.selector";
import { selectView } from "../redux/view/view.selector";
import {getDayPartyById, getCompanyByDescription, getCompanyById, getDayPartByDescription, getLevelByDescription, getLevelById, getViewByDescription, getViewById } from "../utils/Mapper";
import { selectTrack } from "../redux/track/track.selector";
import { addTrack as addTrackApi } from "../services/track";
import { addTrack as addTrackSlice } from "../redux/track/track.slice";
import { getUserByToken } from "../services/user";
import { useNavigate } from "react-router-dom";
import { addStop } from "../services/stop";
import { useLoaderData, useParams } from "react-router-dom"
import {k as apikey} from '../utils/projectdata'
import getAddressByCoordinates from "../services/GoogleMaps.services";
import { useJsApiLoader } from "@react-google-maps/api";
export default function UpdateTrack(){
    const params = useParams()
    const data: any = useLoaderData()
    const [geocoder, setGeocoder] = useState<google.maps.Geocoder|null>(null);
    const { isLoaded } = useJsApiLoader({
        id: 'google-map-script',
        googleMapsApiKey: apikey,
        language: 'he',
      });
    const [isAnitialized, setIsAnitialized]=useState(false)
    const [image, setImage]=useState<Blob>()
    const[imageToShow, setImageToShow]=useState<string>("")
    const [selectedValue, setSelectedValue] = useState('start');
    const token=localStorage.getItem('user')
    const[addressStart, setAddressStart]=useState("")
    const[addressEnd, setAddressEnd]=useState("")
    const[addressStop, setAddressStop]=useState<string[]>([])
  const [address, setAddress]=useState<string>("")
  const[x,setX]=useState(0)
  const[y,setY]=useState(0)
  const[level, setLevel]=useState("")
  const[dayPart, setDayPart]=useState("")
  const[company, setCompany]=useState(1)
  const[view, setView]=useState(1)
  const[stops, setStops]=useState<Omit<Stop,'id'>[]>([])
  const levels=useSelector(selectLevel)
  const dayParts=useSelector(selectDayPart)
  const companies =useSelector(selectCompany)
  const views=useSelector(selectView)
  const tracks=useSelector(selectTrack)
  const dispatch=useDispatch()
  const navigate=useNavigate()
  const[track, setTrack]=useState<Track|null>(null)
  useEffect(() => {
    if (isLoaded) {
      // Initialize the geocoder
      setGeocoder(new window.google.maps.Geocoder());
    }
  }, [isLoaded]);
  useEffect(()=>{
    if(data)
    {
        setTrack(data)
    }
  },[data])
  useEffect(()=>{
    if(!isAnitialized&&geocoder){
        Initialize();
    }
  },[geocoder])
  
  async function Initialize(){
    
    if(track){
        // setTrack(data)
        let start=await getAddressByCoordinates(geocoder,{lat:track.startX,lng:track.startY});
        let end=await getAddressByCoordinates(geocoder,{lat:track.endX,lng:track.endY})
        let tempStops=[] as Omit<Stop, "id">[]
        let tempStopAddresses=[] as string[]
        track.stops.map(async(value)=>{
            let tempAddress=await getAddressByCoordinates(geocoder,{lat:value.x,lng:value.y})
            tempStopAddresses.push(tempAddress)
            let tempStop={
                trackId:track.id,
                x:value.x,
                y:value.y
            }as Omit<Stop,"id">
            tempStops.push(tempStop)
        })
        try {
            console.log("hello3")
            let token=localStorage.getItem('user');
            console.log(token)
            const response = await fetch(`https://localhost:7090/api/Track/${props.track.id}/picture`,{headers:{"Authorization": `Bearer ${token}`}});
            if (response) {
            console.log(response)
            const blob = await response.blob();
            const url = URL.createObjectURL(blob);
            setImageToShow(url)
                setImage(blob)
            } else {
            console.error('Failed to fetch picture');
            }
        } catch (error) {
            console.error('Error fetching picture:', error);
        }
        setAddressStart(start)
        setAddressEnd(end)
        setStops(tempStops)
        setAddressStop(tempStopAddresses)
        setIsAnitialized(true)
    }
  }
  const marks=[
    {
      value:24,
      label:'1 day'
    },
    {
      value:48,
      label:'2 days'
    },
    {
      value:72,
      label:'3 days'
    },
    {
      value:96,
      label:'4 days'
    }
  ]
  
  
  const handleChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    setSelectedValue(event.target.value);
    
  };
    const handleFileChange = (event:any) => {
        const selectedImage = event.target.files[0];
        setImage(selectedImage);
        setImageToShow(URL.createObjectURL(selectedImage))
      };
    useEffect(()=>{
      console.log(selectedValue)
    },[selectedValue])
    useEffect(()=>{
      let tempTrack={...track}as Track
      if(selectedValue==="start"){
        setAddressStart(address)
        tempTrack.startX=x;
        tempTrack.startY=y;
      }else{
        if(selectedValue==="end"){
          setAddressEnd(address)
          tempTrack.endX=x;
          tempTrack.endY=y;
        }else{
          if(selectedValue==="stop"){
            
            setAddressStop([...addressStop,address])
            setStops([...stops,{x:x, y:y, trackId:0}])
          }
        }
      }
      setTrack({...tempTrack})
    },[address])
    function handleMinusStop(index:number){
      console.log(addressStop)
      console.log(index)
      let newAddressesStop=addressStop.slice(0,index).concat(addressStop.slice(index+1))
      console.log(addressStop)
      let newStops=stops.slice(0,index).concat(stops.slice(index+1))
      console.log(newAddressesStop)
      setAddressStop(newAddressesStop)
      setStops(newStops)
    }
    async function handleLengthChange(value:number,index:any){
      let tempTrack={...track}as Track
      tempTrack.length=value
      setTrack({...tempTrack})
      return value.toString();
    }
    const handleSubmit=async(event: React.FormEvent<HTMLFormElement>)=>{
      event.preventDefault();
      const token=localStorage.getItem('user')
      if(token===null||token===undefined||token===''){
        alert("you are not logged in")
        navigate('/')
      }
    
    const dataToSend={
      companyForTripId: getCompanyByDescription(company),
      pictureForm:image,
      viewId:getViewByDescription(view),
      dayPartId:getDayPartByDescription(dayPart),
      levelId:getLevelByDescription(level),
      created:track.created,
      title:track.title,
      description:track.description,
      startX:track.startX,
      startY:track.startY,
      stops:[],
      comments:[],
      endX:track.endX,
      endY:track.endY,
      length:track.length,
      favourites:0,
      userId:0
    }as Omit<Track,'id'>;
    const formData = new FormData(event.currentTarget);
        console.log(formData)
        formData.append('pictureForm', image!);
        formData.append('companyForTripId', dataToSend.companyForTripId.toString());
        formData.append('viewId', dataToSend.viewId.toString());
        formData.append('dayPartId', dataToSend.dayPartId.toString());
        formData.append('levelId', dataToSend.levelId.toString());
        formData.append('length', dataToSend.length.toString());
        formData.append('startX', dataToSend.startX.toString());
        formData.append('startY', dataToSend.startY.toString());
        formData.append('endX', dataToSend.endX.toString());
        formData.append('endY', dataToSend.endY.toString());
        formData.append('favourites', dataToSend.favourites.toString());
        
        formData.append('created', dataToSend.created.toDateString());
        formData.append('length', dataToSend.length.toString());
    console.log(formData)
    formData.forEach((value,key)=>{
      console.log(`${key} : ${value}`)
    })
    // addUser(formData)
    try{
      // const response = await axios.post('/User', formData, {
      //   headers: {
      //     'Content-Type': 'multipart/form-data'
      //   }
      // });

      const response=await (await getUserByToken(token!)).json()
      formData.append('userId', response.id);
    console.log(formData)
      console.log(response)
      console.log('user recieved successfully:', response);
      try{
        const newTrack=await addTrackApi(formData,token!)
        // 
        stops.forEach(async(value:Omit<Stop,'id'>)=>{
          try{
            value.trackId=newTrack.id;
            let newStop=await addStop(value,token!);
            newTrack.stops.push(newStop);
          }catch(error){
              console.log(error)
          }
        });
        dispatch(addTrackSlice(newTrack))
      }catch(error){
        console.log(error)
      }
    } catch (error) {
      console.error('Error sending data:', error);
    }
    }
    
    return<>
    <Box component={'form'} noValidate onSubmit={handleSubmit}>
      <Grid container gap={2} alignItems={"center"} justifyContent={"center"}>
          <Grid xs={12} alignItems={"center"} alignContent={"center"} marginTop={'100px'} gap={3} justifyContent={"space-around"}>
              <Avatar  sx={{ bgcolor: grey[500],marginLeft:'35%',marginRight:'50%',marginBottom:'50px', width:'400px', height:'400px' }} src={imageToShow} variant="rounded"/>
              <input
                    accept="image/*"
                    id="contained-button-file"
                    multiple
                    type="file"
                    onChange={handleFileChange}
                    style={{ display: 'none' }}
                  />
                  <label htmlFor="contained-button-file">
                    <Button variant="contained" color="primary" component="span">
                      Upload Image
                    </Button>
                  </label>
          </Grid>
          <Grid item xs={12} sx={{display:"block", marginTop:'0px'}}>
                <TextField
                  autoComplete="title"
                  name="title"
                  required
                  
                  id="title"
                  label="title"
                  autoFocus
                />
              </Grid>
              <Grid item xs={12} display={"block"}>
                <TextField
                  autoComplete="description"
                  name="description"
                  required
                  multiline
                  
                  rows={4}
                  id="description"
                  label="description"
                  autoFocus
                />
              </Grid>
          <Grid xs={3} >
          <TextField
          sx={{paddingBottom:'15px'}}
                    required
                    fullWidth
                    name="addressStart"
                    label="Address Start"
                    type="address"
                    id="addressStart"
                    autoComplete="new-address"
                    value={addressStart}
                  />
                  
          {addressStop&&addressStop.map((stop,index)=>{
            return <><div style={{display:"inline"}}><TextField
                      required
                      fullWidth
                      
                      name="addressStop"
                      label="Address Stop"
                      type="address"
                      id={index.toString()}
                      key={index}
                      autoComplete="new-address"
                      value={stop}
                      sx={{display:"inline",marginBottom:'5px'}}
                    /></div>
                    <div style={{display:"inline"}}><Button onClick={()=>{handleMinusStop(index)}} sx={{display:"inline"}}>-</Button></div>
                    </>
          })}
          <TextField
          sx={{marginBottom:'5px'}}
                    required
                    fullWidth
                    name="addressEnd"
                    label="Address End"
                    type="address"
                    id="addressEnd"
                    autoComplete="new-address"
                    value={addressEnd}
                  />
          </Grid>
          <Grid xs={3}>
              <FormControl>
                <FormLabel id="demo-radio-buttons-group-label">Choose track points</FormLabel>
                <RadioGroup
                  aria-labelledby="demo-radio-buttons-group-label"
                  defaultValue="start"
                  name="radio-buttons-group"
                  onChange={handleChange}
                >
                  <FormControlLabel value="start" control={<Radio />} label="Start" />
                  <FormControlLabel value="stop" control={<Radio />} label="Stop" />
                  <FormControlLabel value="end" control={<Radio />} label="End" />
                </RadioGroup>
              </FormControl>
          </Grid>
          <Grid xs={3}>
              <Map setAddress={setAddress} setX={setX} setY={setY}/>
          </Grid>
          <Grid item xs={12} >
                <Autocomplete
                disablePortal
                id="level"
                // inputValue={experiences}
                inputValue={level}
                onInputChange={(event, newInputValue) => {
                  setLevel(newInputValue);
                  console.log(newInputValue)
                }}
                        // onClick={onClick}
                options={levels}
                sx={{ width: 300 }}
                renderInput={(params) => <TextField {...params} label="Level" />}
                />
           </Grid>
           <Grid item xs={12} >
                <Autocomplete
                disablePortal
                id="dayPart"
                // inputValue={experiences}
                inputValue={dayPart}
                onInputChange={(event, newInputValue) => {
                  setDayPart(newInputValue);
                  console.log(newInputValue)
                }}
                        // onClick={onClick}
                options={dayParts}
                sx={{ width: 300 }}
                renderInput={(params) => <TextField {...params} label="Day Part" />}
                />
           </Grid>
           <Grid item xs={12} >
                <Autocomplete
                disablePortal
                id="company"
                // inputValue={experiences}
                inputValue={company}
                onInputChange={(event, newInputValue) => {
                  setCompany(newInputValue);
                  console.log(newInputValue)
                }}
                        // onClick={onClick}
                options={companies}
                sx={{ width: 300 }}
                renderInput={(params) => <TextField {...params} label="Company" />}
                />
           </Grid>
           <Grid item xs={12} >
                <Autocomplete
                disablePortal
                id="view"
                // inputValue={experiences}
                inputValue={view}
                onInputChange={(event, newInputValue) => {
                  setView(newInputValue);
                  console.log(newInputValue)
                }}
                        // onClick={onClick}
                options={views}
                sx={{ width: 300 }}
                renderInput={(params) => <TextField {...params} label="View" />}
                />
           </Grid>
          <Grid xs={12}>
            <Slider
              aria-label="length"
              defaultValue={5}
              valueLabelDisplay="auto"
              // getAriaValueText={handleLengthChange}
              shiftStep={1}
              step={1}
              marks={marks}
              min={1}
              max={100}
              onChange={(event, newInputValue) => {
                let tempTrack={...track}
                tempTrack.length=parseInt(newInputValue.toString());
                setTrack({...tempTrack})
                console.log(tempTrack)
              }}
            />
          </Grid>
          
      </Grid>
      <Button
                type="submit"
                // fullWidth
                variant="contained"
                
                sx={{ mt: 3, mb: 2 , width:'300px'}}
              >
                Sign Up
              </Button>
    </Box>
    </>
}
