  id: number;
}

const Picture: React.FC<PictureProps> = (props:{ id:number }) => {
  const [pictureUrl, setPictureUrl] = useState<string | null>(null);
  const user=useAppSelector(selectUser)
 
  useEffect(() => {
    const fetchPicture = async () => {
      console.log(props.id)
        if(props.id!=0){
      // try {
