      // console.log(response)

      console.log(user?.pictureBytes)
          const blob =  stringToBlob(user?.pictureBytes,null,null);
          const url = URL.createObjectURL(blob);
          setPictureUrl(url);
    }
    };

    fetchPicture();

    // Clean up URL object when component unmounts
    return () => {
      if (pictureUrl) {
        URL.revokeObjectURL(pictureUrl);
      }
    };
  },[props.id]);

  if (!pictureUrl) {
    return <div>Loading...</div>;
  }

  return <>
  {/* <img src={pictureUrl} alt="mdo" width="32" height="32" className="rounded-circle"/> */}
  <Avatar alt="Remy Sharp" src={pictureUrl} >
    <img  src={pictureUrl}/></Avatar></>;
};

export default Picture;
