import axios from '../utils/axios.util'
import { Stop} from "../types/Stop"

export const getStops = async () => {
    const response = await fetch('https://localhost:7127/api/Stop')
    // לקריאת get & delete אין body
    // const response = await fetch('https://jsonplaceholder.typicode.com/todos', { method: 'POST', body: JSON.stringify({}) })
    const stops = await response.json()
    
    return stops
}

export const addStop= async (stop: Omit<Stop, 'id'>,token:string) => {
    const response = await axios.post('/Stop', stop,{
        headers:{
            "Authorization":`Bearer ${token}`
        }
    })

    return response
}

export const updateStop = async (stop: Stop) => {
    const response = await axios.put(`/Stop/${stop.id}`, stop)
    const updatedStop = response.data
    return updatedStop
}

export const deleteStop = async (id: number,token:string) => {
    const response = await axios.delete(`/Stop/${id}`,{headers:{Authorization:`Bearer ${token}`}})
    return response
}
