import axios from '../utils/axios.util'
import { User} from "../types/user.types"

export const getUsers = async () => {
    const response = await fetch('https://localhost:7090/api/User')
    console.log(response)
    // לקריאת get & delete אין body
    // const response = await fetch('https://jsonplaceholder.typicode.com/todos', { method: 'POST', body: JSON.stringify({}) })
    const users = await response.json()
    
    return users
}

export const getUser = async (id:number) => {
    
    const response = await fetch(`https://localhost:7090/api/User/${id}`)
    // לקריאת get & delete אין body
    // const response = await fetch('https://jsonplaceholder.typicode.com/todos', { method: 'POST', body: JSON.stringify({}) })
    const user = await response.json()
    
    return user
}

export const addUser= async (user:FormData) => {
    // console.log(user)
    const response = await axios.post('/User', user, {
        headers: {
          'Content-Type': 'multipart/form-data'
        }
      });
    const newUser = response.data
    return newUser
}

export const updateUser = async (user: User) => {
    const response = await axios.put(`/User/${user.id}`, user)
    const updatedUser = response.data
    return updatedUser
}

export const deleteUser = async (id: number) => {
    const response = await axios.delete(`/User/${id}`)
    return response
}
