import { User } from "../../types/user.types";
import { addUser as addUserApi } from '../../services/user'
import { AppThunk } from "../store";
import { addUser as addUserAction } from './user.slice'

export const adduser = (post: Omit<User,'id'>): AppThunk<User> => async (dispatch, getState) => {
    // const state = getState()// אם רוצים לקבל מידע מהרידקס כדי לשלוח לקריאת שרת
    const newPost = await addUserApi(post)
    dispatch(addUserAction(newPost))
    return newPost
}
