import React from 'react';
import { Navigate as BigNav, Navigate, createBrowserRouter } from "react-router-dom";
// import Layout from "./Layout/Layout";
// import Home from "./component/General/home";
// import Login from "./component/General/Login";
// import SignIn from './component/General/SignIn';
// import MyProfile from "./component/User/myProfile";

import Layout from "../layouts/Layout";
// import React from 'react';
import HomePage from "../pages/HomePage";
import SignUpForm from '../sections/SignUpForm';
import SignUpPage from '../pages/SignUpPage';
import TrackPage from '../pages/TracksPage';
import MyTracks from '../sections/MyTracks';
import AddTrackPage from '../pages/AddTrackPage';
import MyTracksPage from '../pages/MyTracksPage';
import UpdateTrack from '../sections/UpdateTrack';
import { getTrackByArea, getTrackById } from '../services/track';
import UpdateUser from '../sections/UpdateUser';
import { getUser, getUserByToken } from '../services/user';
import MyFavoritesPage from '../pages/MyFavoritesPage';
export const router = createBrowserRouter([
    {
        path: '/',
        element:<Layout/>,
        children: [
            {
                path: '/',
                element: <HomePage/>,
            },
            
            {
                path: 'user/',
                // element: <h1>Products</h1>
                children:[
                    
                    {
                        path:'myTracks/',
                        children:[
                            {
                                path:'',
                                element:<MyTracksPage/>,
                                loader: async()=>{
                                    let token=localStorage.getItem('user')
                                    if(token!==undefined&&token!==null&&token!==""){
                                        try {
                                            console.log("loading my traccks")
                                            
                                            console.log(token)
                                            const response = await fetch(`https://localhost:7090/api/User/UserByToken`,{headers:{"Authorization": `Bearer ${token}`}});
                                            if (response) {
                                                let id=(await response.json()).id
                                                console.log(id)
                                                try {
                                                    // console.log("hello3")
                                                    // let token=localStorage.getItem('user');
                                                    // console.log(token)
                                                    const response2 = await fetch(`https://localhost:7090/api/Track/MyTracks/${id}`,{headers:{"Authorization": `Bearer ${token}`}});
                                                    if (response2) {
                                                        let res=await response2.json()
                                                        console.log(res)
                                                        //   dispatch(getAllTracks(res))
                                                        return res;
                                                    
                                                    } else {
                                                        console.error('Failed to fetch picture');
                                                    }
                                                } catch (error) {
                                                    console.error('Error fetching picture:', error);
                                                }
                                            } else {
                                                console.error('Failed to fetch picture');
                                            }
                                        } catch (error) {
                                            console.error('Error fetching picture:', error);
                                        }

                                    }
                                }
                            },
                            {
                                path:'updateTrack/:id',
                                element:<UpdateTrack/>,
                                loader: async ({ params }) => {
                                    console.log("edit")
                                    const track = await getTrackById(Number(params.id!))
                                    console.log(track)
                                    return track
                
                                },
                            },
                            {
                                path:'addTrack',
                                element:<AddTrackPage/>
                            }
                        ]
                    },
                    {
                        path:'favorites',
                        element:<MyFavoritesPage/>,
                        loader:async() => {
                            const token=localStorage.getItem('user')
                            const user=await (await getUserByToken(token!)).json()
                            const u=await getUser(user.id)
                            console.log(u)
                            return u.favourites;
        
                        }
                    },
                    {
                        path:'myProfile',
                        element:<UpdateUser />,
                        loader:async() => {
                            const token=localStorage.getItem('user')
                            const user=await (await getUserByToken(token!)).json()
                            const u=await getUser(user.id)
                            return u;
        
                        }
                    },
                    {
                        path:'updateProfile'
                    }
                    
                ]
            },
            {
                path: '/SignIn',
                element: <SignUpPage/>
            },
            {
                path: '/Login',
                // element: <Login/>
            },
            {
                path: '/findTrack',
                element:<TrackPage/>,
                loader:async() => {
                    console.log("find track loader")
                    let tracks=await getTrackByArea("north");
                    return tracks
                }
            },
            {
                path:'/viewTrack',
                
            }
            

        ]
    },
   
    {
        path: '*',
        element: <Navigate to="/"/>,
    },

]);
