import React from "react";
import Autocomplete from "@mui/material/Autocomplete";
import TextField from "@mui/material/TextField";
import { useSelector } from "react-redux";
import { selectStatus } from "../redux/status/status.selector";

const MyAutocomplete = (props:{ onChange:(value:any)=>void }) => {
    const status = useSelector(selectStatus)
    const handleInputChange = (event:any, option) => {
        if (option) {
          // If an option is selected, pass its value to the onChange function
          props.onChange(option.value);
        } else {
          // If no option is selected (i.e., when the user clears the input), pass null to the onChange function
          props.onChange(null);
        }
      };

  return (
    <Autocomplete
      disablePortal
      id="status"
      options={status}
      sx={{ width: 300 }}
      onChange={handleInputChange} // Pass the handleInputChange function to onChange prop
      renderInput={(params) => <TextField {...params} />}
    />
  );
};

export default MyAutocomplete;
