import { Button, TextField } from "@mui/material";
import { ChangeEvent, useEffect, useState } from "react";
import { Login, getUserByToken } from "../../services/user";
import { Login as LoginThunk } from "../../redux/user/user.thunk";
import { addUser } from "../../redux/user/user.slice";
import { useDispatch } from "react-redux";
import { isValidEmail } from "../../utils/validation.utils";
import { useAppDispatch } from '../../redux/store';
import BasicModal from "../../components/Modal.component";
export default function LoginForm(){
    const [email, setEmail]=useState("")
    const [flag,setFlag]=useState(true)
    const dispatch=useAppDispatch()
    const [open,setOpen]=useState(false)
    const [text,setText]=useState("")
    const onChangeEndler=(e:ChangeEvent<HTMLInputElement>)=>{
        console.log(e.target)
        let value=e.target.value
        setEmail(value)
    }
    
    const onLoginHandler=async()=>{
        if(email===""){
            alert("please enter an email address")
        }else{
            if(isValidEmail(email)){
            console.log("hello")
            try{
            setOpen(true)
            setText("searching for you in the server")
            const token=await Login(email);
            localStorage.setItem('user',token);
            setText("token has been generated!")
            // console.log()
            // await getUserApi(token.data)
            dispatch(LoginThunk(token))
            setText("Happy Serving!")
            setOpen(false)
            setText("")
            }catch(error){
                alert("you were not found in the system\nplease sign up!")
            }
            }else{
                alert("please enter a valid email address to login")
            }
        }
    }
    async function getUserApi(token:any){
        try{
        const response=await getUserByToken(token)
        console.log(response)
        if(response.status){
        dispatch(addUser(await response.json()))
        }
    }catch(error){
        alert(error)
    }
        
}
    useEffect(()=>{
        console.log(email)
    },[email])

    return<> 
    <BasicModal open={open} text={text}/>    
    <TextField 
        error={email!==""&&!isValidEmail(email)}
        size='small' 
        sx={{backgroundColor:'whitesmoke', borderRadius:'5px'}} 
        id="outlined-basic" 
        label="your email here"  
        variant='outlined'
        onChange={onChangeEndler}
        helperText={email===""||isValidEmail(email)?"":"please enter a valid email address"}
    />
    <Button color="inherit" onClick={onLoginHandler}>Login</Button>
</>
}
