import * as React from 'react';
import Card from '@mui/material/Card';
import CardActions from '@mui/material/CardActions';
import CardContent from '@mui/material/CardContent';
import CardMedia from '@mui/material/CardMedia';
import Button from '@mui/material/Button';
import Typography from '@mui/material/Typography';

import { useNavigate } from 'react-router-dom';
import { Avatar } from '@mui/material';

export default function CardRound(props: { image: any, title: string, text: string, url: string }) {
  const navigate = useNavigate();
  
  const onClickHandler = () => {
    navigate(props.url);
  }

  return (
    <Card sx={{
      width: 250, // Set a fixed width and height for the circular card
      height: 250, 
      borderRadius: '50%', // Make the whole card circular
      overflow: 'hidden',   // Ensures no content sticks out of the rounded corners
      boxShadow: 3,         // Optional: Adds a shadow to make the card pop
      display: 'flex',
      flexDirection: 'column',
      alignItems: 'center',
      justifyContent: 'center',
    }}>
      <CardMedia
        sx={{
          width: 120, // Set the image width to fit within the circle
          height: 120, // Set the image height to fit within the circle
          borderRadius: '50%', // Make the image circular
          objectFit: 'cover'   // Ensures the image fits well within the circle
        }}
        image={props.image}
        title={props.title}
      />
      <CardContent sx={{
        padding: 1, // Optional: Adjust the padding to center the text nicely
        textAlign: 'center'
      }}>
        <Typography gutterBottom variant="h6" component="div">
          {props.title}
        </Typography>
        <Typography variant="body2" color="text.secondary">
          {props.text}
        </Typography>
      </CardContent>
      <CardActions sx={{
        display: 'flex',
        justifyContent: 'center',
        marginBottom: 1
      }}>
        <Button size="small" onClick={onClickHandler}>Learn More</Button>
      </CardActions>
    </Card>
  );
}
