import * as React from 'react';
import Avatar from '@mui/material/Avatar';
import Button from '@mui/material/Button';
import CssBaseline from '@mui/material/CssBaseline';
import TextField from '@mui/material/TextField';
import FormControlLabel from '@mui/material/FormControlLabel';
import Checkbox from '@mui/material/Checkbox';
import Link from '@mui/material/Link';
import Grid from '@mui/material/Grid';
import Box from '@mui/material/Box';
import axios from '../utils/axios.util';
import LockOutlinedIcon from '@mui/icons-material/LockOutlined';
import Typography from '@mui/material/Typography';
import Container from '@mui/material/Container';
import { createTheme, ThemeProvider } from '@mui/material/styles';
import { Autocomplete, AutocompleteChangeDetails, Input } from '@mui/material';
import { useDispatch, useSelector } from 'react-redux';
import { selectExperience } from '../redux/experience/experience.selector';
import { selectStatus } from '../redux/status/status.selector';
import { useEffect, useState } from 'react';
import Map from '../components/Map';
import exp from 'constants';
import { DoNotStepOutlined } from '@mui/icons-material';
import { User } from '../types/user.types';
import { getExperienceByDescription, getExperienceById, getStatusByDescription, getStatusById } from '../utils/Mapper';
import { selectUser } from '../redux/user/user.selector';
import { addUser } from '../redux/user/user.thunk';
import { addUser as addUserApi, Login, updateUser as UpdateUserApi } from '../services/user';
import { addUser as addUserSlice ,updateUser as UpdateUserSlice} from '../redux/user/user.slice';
import { useLoaderData, useParams } from "react-router-dom"
import getAddressByCoordinates from '../services/GoogleMaps.services';
import { useJsApiLoader } from '@react-google-maps/api';
import {k as apikey} from '../utils/projectdata';
// function Copyright(props: any) {
//   return (
//     <Typography variant="body2" color="text.secondary" align="center" {...props}>
//       {'Copyright Â© '}
//       <Link color="inherit" href="https://mui.com/">
//         Your Website
//       </Link>{' '}
//       {new Date().getFullYear()}
//       {'.'}
//     </Typography>
//   );
// }

// TODO remove, this demo shouldn't need to reset the theme.
const defaultTheme = createTheme();

export default function UpdateUser() {
    const params=useParams();
    const mydata:any=useLoaderData();

    const experiences=useSelector(selectExperience)
    const status=useSelector(selectStatus)
    const [address, setAddress]=useState("")
    const dispatch =useDispatch();
    const user=useSelector(selectUser)
    const [imageToShow, setImageToShow]=useState<string | undefined>(undefined)
    console.log(experiences)
    const [x, setX]=useState(0)
    const [y, setY]=useState(0)
    const [st,setSt]=useState<string>("single")
    const [ex,setEx]=useState<string>("junior")
    const [image, setImage]=useState<any>();
    const [geocoder, setGeocoder] = useState<google.maps.Geocoder|null>(null);
const [isInit,setIsInit]=useState(false)

    const { isLoaded } = useJsApiLoader({
        id: 'google-map-script',
        googleMapsApiKey: apikey,
        language: 'he',
      });
    console.log(ex)
    // console.log("hello")
    useEffect(() => {
        if (isLoaded) {
          // Initialize the geocoder
          setGeocoder(new window.google.maps.Geocoder());
        }
      }, [isLoaded]);
      init();
    useEffect(()=>{
        // if(data&&geocoder){
        //     init();
        // }
    },[mydata,geocoder])
    async function init(){
        console.log(mydata)
        if(!isInit&&mydata&&geocoder){
            console.log("init")
        let ad;
        try{
            ad=await getAddressByCoordinates(geocoder, {lat:mydata.adressX,lng:mydata.adressY});
        }catch(e){
            console.log(e)
            ad="pick home address"
        }
        setAddress(ad);
        setEx(getExperienceById(mydata.experienceId))
        try {
            console.log("hello3")
            let token=localStorage.getItem('user');
            console.log(token)
            const response = await fetch(`https://localhost:7090/api/User/${mydata.id}/picture`,{headers:{"Authorization": `Bearer ${token}`}});
            if (response) {
              console.log(response)
              const blob = await response.blob();
              const url = URL.createObjectURL(blob);
              setImageToShow(url);
              setImage(blob)
            } else {
              console.error('Failed to fetch picture');
            }
          } catch (error) {
            console.error('Error fetching picture:', error);
          }
          setSt(getStatusById(mydata.statusId))
          setX(mydata.adressX)
          setY(mydata.adressY)
          
          setIsInit(!isInit)
        }
          
    }
  const handleSubmit = async(event: React.FormEvent<HTMLFormElement>) => {
    event.preventDefault();
    const mytoken=localStorage.getItem('user')
    const data = new FormData(event.currentTarget);
    const dataToSend={
        id:mydata.id,
        pictureFile:image,
        myTracks:mydata.tracks,
        favourites:mydata.favourites,
      mail: data.get('mail'),
      profilePicture:imageToShow,
      experienceId:getExperienceByDescription(ex),
      statusId:getStatusByDescription( st),
      birthDate:data.get('birthDate'),
      name:data!.get('Name'),
      addressX:x,
      addressY:y
    }as User;
    const formData = new FormData();
        formData.append('name',dataToSend.name);
        formData.append('mail',mydata.mail);
        formData.append('id',mydata.id);
        formData.append('profilePicture',dataToSend.pictureFile)
        
        formData.append('pictureFile', image!);
        formData.append('experienceId', getExperienceByDescription(ex).toString());
        formData.append('statusId', getStatusByDescription(st).toString());
        formData.append('birthDate', (data.get('birthDate')!==''&&data.get('birthDate')!==undefined&&data.get('birthDate')!==null)?data.get('birthDate'):mydata.birthDate.toString());
        formData.append('adressX', x.toString());
        formData.append('adressY', y.toString());
    console.log(formData)
    console.log(formData.get('birthDate'))
    formData.forEach((value,key)=>{
      console.log(`${key} : ${value}`)
    })
    // addUser(formData)
    try{
      // const response = await axios.post('/User', formData, {
      //   headers: {
      //     'Content-Type': 'multipart/form-data'
      //   }
      // });
      const response=await UpdateUserApi(mydata.id,formData,mytoken!)
      dispatch(UpdateUserSlice(dataToSend))
      console.log(response)
    //   const token=await Login(response.mail)
    //   console.log(token)
    //   localStorage.setItem('user',token.data)
    //   console.log('Data sent successfully:', response);
    } catch (error) {
      console.error('Error sending data:', error);
    }

  };
  const handleFileChange = (event:any) => {
    const selectedImage = event.target.files[0];
    setImage(selectedImage);
    setImageToShow(URL.createObjectURL(selectedImage))
  };

useEffect(()=>{
    
    console.log("hello")
});
// Listen for the 'change' event


  return (
    <ThemeProvider theme={defaultTheme}>
      <Container component="main" maxWidth="xs">
        <CssBaseline />
        <Box
          sx={{
            marginTop: 8,
            display: 'flex',
            flexDirection: 'column',
            alignItems: 'center',
          }}
        >
          {/* <Avatar sx={{ m: 1, bgcolor: 'secondary.main' }}>
            <LockOutlinedIcon />
          </Avatar> */}
          {image && <Avatar alt="Uploaded" src={imageToShow} style={{ width: 150, height: 150 }} />}
          <Grid item xs={12}>
                <input
                  accept="image/*"
                  id="contained-button-file"
                  multiple
                  type="file"
                  onChange={handleFileChange}
                  style={{ display: 'none' }}
                />
                <label htmlFor="contained-button-file">
                  <Button variant="contained" color="primary" component="span">
                    Upload Image
                  </Button>
                </label>
              </Grid>
          <Typography component="h1" variant="h5">
            My Profile
          </Typography>
          <Box component="form" noValidate onSubmit={handleSubmit} sx={{ mt: 3 }}>
            <Grid container spacing={2}>
            <Grid item xs={12}>
          {/* Display the selected image as Avatar */}
          
        </Grid>
              <Grid item xs={12} >
                <TextField
                  autoComplete="given-name"
                  name="Name"
                  defaultValue={mydata.name}
                  required
                  fullWidth
                  id="Name"
                  label="Name"
                  autoFocus
                />
              </Grid>
              
              <Grid item xs={12} >
                <Autocomplete
                disablePortal
                id="experience"
                defaultValue={getExperienceById(mydata.experienceId)}
                // inputValue={experiences}
                inputValue={ex}
                onInputChange={(event, newInputValue) => {
                  setEx(newInputValue);
                  console.log(newInputValue)
                }}
                        // onClick={onClick}
                options={experiences}
                sx={{ width: 300 }}
                renderInput={(params) => <TextField {...params} label="Experience" />}
                />
              </Grid>
              <Grid item xs={12} >
              <Autocomplete
                disablePortal
                id="status"
                options={status}
                defaultValue={getStatusById(mydata.statusId)}
                sx={{ width: 300 }}
                inputValue={st}
                onInputChange={(event, newInputValue) => {
                  setSt(newInputValue);
                  console.log(newInputValue)
                }}   
                renderInput={(params) => <TextField {...params} label="Status" />}
                />
              </Grid>
              <Grid item xs={12}>
                <TextField
                  required
                  fullWidth
                  disabled
                  defaultValue={mydata.mail}
                  id="email"
                  label="Email Address"
                  name="mail"
                  autoComplete="email"
                />
              </Grid>
              <Grid item xs={12}>
                <TextField
                  required
                  fullWidth
                  defaultValue={new Date(mydata.birthDate)}
                  id="birthDate"
                  label="Birth Date"
                  name="birthDate"
                  autoComplete="birthDate"
                  type="date"
                />
              </Grid>
              <Grid item xs={12}>
                <Map setAddress={setAddress} setX={setX} setY={setY}/>
              </Grid>
              <Grid item xs={12}>
                <TextField
                  required
                  fullWidth
                  name="address"
                  label="Address"
                  type="address"
                  id="address"
                  autoComplete="new-address"
                  value={address}
                />
              </Grid>
              <Grid item xs={12}>
                <FormControlLabel 
                  control={<Checkbox value="allowExtraEmails" color="primary" />}
                  label="I want to receive inspiration, marketing promotions and updates via email."
                />
              </Grid>
            </Grid>
            <Button
              type="submit"
              fullWidth
              variant="contained"
              
              sx={{ mt: 3, mb: 2 }}
            >
              Update Profile
            </Button>
            <Grid container justifyContent="flex-end">
              <Grid item>
                <Link href="#" variant="body2">
                  Already have an account? Sign in
                </Link>
              </Grid>
            </Grid>
          </Box>
        </Box>
        {/* <Copyright sx={{ mt: 5 }} /> */}
      </Container>
    </ThemeProvider>
  );
}
