import { Outlet } from "react-router-dom"
import Header from './Header';
import Footer from "./Footer";
import { getUser as getUserApi} from "../services/user"
import {selectUser} from '../redux/user/user.selector'
import { useDispatch, useSelector } from "react-redux"
import { useEffect, useState } from "react"
export default function Layout(){
    const user:any =undefined//useSelector(selectUser)
    const dispatch=useDispatch();
    const [component, setComponent]=useState(<Header id={undefined}/>)
    // alert("hello")
    
    useEffect(()=>{
        if(user!==undefined&&user!==null&&user.id!==0){
            setComponent(<Header id={user!.id}/>)
        }else{
            setComponent(<Header id={undefined}/>)
        }
    },[user])
    // const component= await returnComp();
    
    return<>
     <header>{component}</header>
        {/* <main><Outlet /></main> */}
        <footer><Footer/></footer>
    </>
}
