import * as React from 'react';
import Box from '@mui/material/Box';
import Drawer from '@mui/material/Drawer';
import Button from '@mui/material/Button';
import List from '@mui/material/List';
import Divider from '@mui/material/Divider';
import ListItem from '@mui/material/ListItem';
import ListItemButton from '@mui/material/ListItemButton';
import ListItemIcon from '@mui/material/ListItemIcon';
import ListItemText from '@mui/material/ListItemText';
import InboxIcon from '@mui/icons-material/MoveToInbox';
import MailIcon from '@mui/icons-material/Mail';
import { Grid, IconButton, Stack, TextField } from '@mui/material';
import CommentIcon from '@mui/icons-material/Comment';
import { useEffect, useState } from 'react';
import { getCommentsByTrackId } from '../services/comment';
import { Comment } from '../types/Comment';
import Picture from './UserProfileImage';
import { Form } from 'react-router-dom';
import SendIcon from '@mui/icons-material/Send';
export default function CommentsDrawer(props:{trackId:number}) {
  const [open, setOpen] = React.useState(false);
    const [uploaded,setUploaded]=useState(true)
    const [comments, setComments]=useState<Comment[]|null>(null)
    const[newComment, setNewComment]=useState("")
    const[drawerContext, setDrawerContext]=useState(<Box/>)
  const toggleDrawer = (newOpen: boolean) => () => {
    setOpen(newOpen);
    newOpen&&uploadComments();
  };
//   uploadComments();
  async function uploadComments(){
    console.log(uploaded)
    // if(!uploaded){
        console.log("hi")
        const commentsResponse=await getCommentsByTrackId(props.trackId);
        console.log(commentsResponse)
        setComments(commentsResponse)
        // if(commentsResponse){
        //     setUploaded(!uploaded)
        // }
    // }
  }
  const handleInputChange=(e: React.ChangeEvent<HTMLInputElement>)=>{
    setNewComment(e.target.value)
  }
  useEffect(()=>{
    if(comments){
        const DrawerList = (
            <Stack spacing={4}>
            <Box  sx={{ width: 500 }} role="presentation" onClick={toggleDrawer(false)}>
              <List >
                {comments?.map((comment:Comment, index) => (
                    <Box >
                    <Divider textAlign='right' >posted on: {comment.postDate.toString()}</Divider>
                  <ListItem key={comment.id} disablePadding dir='rtl' >
                    
                      {/* <ListItemIcon>
                        {/* <Picture id={comment.userId}/> 
                      </ListItemIcon> */}
                      <ListItemText dir='rtl' primary={comment.context}  />
                    
                  </ListItem>
                  </Box>
                ))}
              </List><Divider  />
               </Box>
               
              
              <Box component={Form}>
              <Grid container>
                <Grid item xs={1}>
                    <IconButton dir='rtl'>
                        <SendIcon />
                      </IconButton>
                
                </Grid>
                <Grid item xs={11}>
                    <TextField
                    dir='rtl'
                    label="your comment here"
                    id="outlined-size-small"
                    defaultValue="your comment here"
                    fullWidth
                    onChange={handleInputChange}
                    />  
                </Grid>
              </Grid>
              </Box>
           </Stack>
          );
          setDrawerContext(DrawerList);
    }
  },[comments])
  
useEffect(()=>{
console.log(comments)
console.log(drawerContext)
},[drawerContext])
  return (
    <div>
      <IconButton onClick={toggleDrawer(true)} aria-label="edit">
        <CommentIcon/>
        </IconButton>
      <Drawer
    //   sx={{backgroundColor:'grey'}} 
      anchor={'right'} open={open} onClose={toggleDrawer(false)}>
        {drawerContext}
      </Drawer>
    </div>
  );
}
