import axios from '../utils/axios'
import { User } from "../types/user.types"
import originalAxios from 'axios'
import { Track } from '../types/Track'

export const getUsers = async () => {
    const response = await axios.get('/User')
    const users =  response.data
    return users
}
export const getUserByToken = async (token: string) => {
    const response = await axios.get(`https://localhost:7090/api/User/UserByToken`, { headers: { "Authorization": `Bearer ${token}` } });
    return response
}

export const getUser = async (id: number, token: string) => {
    const response = await fetch(`https://localhost:7090/api/User/${id}`, { headers: { "Authorization": `Bearer ${token}` } })
    const user = await response.json()
    return user
}

export const addUser = async (user: FormData) => {
    const response = await axios.post('/User', user, {
        headers: {
            'Content-Type': 'multipart/form-data'
        }
    });
    const newUser = response.data
    return newUser
}

export const updateUser = async (id: number, user: FormData, token: string) => {
    const response = await originalAxios.put(`https://localhost:7090/api/User/${id}`, user, {
        headers: {
            "Content-Type": 'multipart/form-data',
            Authorization: `Bearer ${token}`,
        }
    });
    const updatedUser = response.data
    return updatedUser
}
export const addFavorite = async (id: number, favorite: Track, token: string) => {
    const response = await originalAxios.put(`https://localhost:7090/api/User/AddFavorite/${id}`, favorite, {
        headers: {
            "Content-Type": 'application/json',
            Authorization: `Bearer ${token}`,
        }
    });
    const updatedUser = response.data
    return updatedUser
}
export const deleteFavorite = async (id: number, favorite: Track, token: string) => {
    const response = await originalAxios.put(`https://localhost:7090/api/User/DeleteFavorite/${id}`, favorite, {
        headers: {
            "Content-Type": 'application/json',
            Authorization: `Bearer ${token}`,
        }
    });
    const updatedUser = response.data
    return updatedUser
}
export const deleteUser = async (id: number) => {
    const response = await axios.delete(`/User/${id}`)
    return response
}

export const Login = async (mail: string) => {
    const response = await originalAxios.post(`https://localhost:7090/Login/${mail}`)
    return response.data
}
