import React, { useState, useEffect } from 'react';
import {getUsers} from '../services/user';
import Avatar from '@mui/material/Avatar';
import axios from '../utils/axios.util';
// import { makeStyles } from '@mui/styles';

// const useStyles = makeStyles((theme:any) => ({
//   avatar: {
//     width: theme.spacing(7), // Adjust the size of the avatar as needed
//     height: theme.spacing(7),
//     overflow: 'hidden', // Ensure the image doesn't overflow the Avatar boundary
//     borderRadius: '50%', // Ensure the Avatar is circular
//     display: 'flex',
//     alignItems: 'center',
//     justifyContent: 'center',
//   },
//   image: {
//     width: '100%',
//     height: '100%',
//     objectFit: 'cover', // Make the image cover the entire area of the Avatar
//   },
// }));
interface PictureProps {
  id: number;
}

const Picture: React.FC<PictureProps> = ({ id }) => {
  const [pictureUrl, setPictureUrl] = useState<string | null>(null);
 
  useEffect(() => {
    const fetchPicture = async () => {
        if(id!==0){
      try {
        const myHeaders = new Headers();
myHeaders.append("", "");
myHeaders.append("Authorization", `Bearer `);
        const response = await fetch(`https://localhost:7090/api/User/${id}/picture`);
        if (response.ok) {
          const blob = await response.blob();
          const url = URL.createObjectURL(blob);
          setPictureUrl(url);
        } else {
          console.error('Failed to fetch picture');
        }
      } catch (error) {
        console.error('Error fetching picture:', error);
      }
    }
    };

    fetchPicture();

    // Clean up URL object when component unmounts
    return () => {
      if (pictureUrl) {
        URL.revokeObjectURL(pictureUrl);
      }
    };
  }, [id]);

  if (!pictureUrl) {
    return <div>Loading...</div>;
  }

  return <>
  {/* <img src={pictureUrl} alt="mdo" width="32" height="32" className="rounded-circle"/> */}
  <Avatar alt="Remy Sharp" src={pictureUrl} >
    <img  src={pictureUrl}/></Avatar></>;
};

export default Picture;
