import { ImageList, ImageListItem, Typography } from '@mui/material';
import * as React from 'react';
import { IconButton } from '@mui/material';
import DownloadIcon from '@mui/icons-material/Download';
import { useParams } from 'react-router-dom';
import diet from '../images/diet3.jpeg'
import todo from '../images/todo.jpg'
import budget from '../images/budget.png'
function DownloadButton(props:{url:string, name:string}) {
  const handleDownload = () => {
    // Logic for handling download
    const link = document.createElement('a');
    link.href = props.url;  // Replace with your file URL
    link.download = props.name;  // Specify the file name
    link.click();
  };

  return (
    <IconButton onClick={handleDownload} color="primary">
        Download
      <DownloadIcon />
    </IconButton>
  );
}
function ProjectContent( props:{image:any, title:string,explain:string, fileUrl:string }){
return <>

          <img
            src={props.image}
            alt={props.fileUrl}
            style={{ maxWidth: '100%', maxHeight: '100%', objectFit: 'contain' }}
          />
       
 <Typography variant="h4">
    {props.title}
 </Typography>
 <Typography variant="body1">
    {props.explain}
 </Typography>
 <DownloadButton url={props.fileUrl} name={props.title}/>
</>
}

function Project(){
const params:any=useParams()
if(params=="diet"){
return <ProjectContent image={diet} title="מחשבון קלוריות" explain={`מכירים אתה הקושי בשמירה על דיאטה? את הקושי בלעקוב אחר התפריט היומי?${<br></br>} בקובץ אקסל זה תמצאו מספר גליונות שיעזרו לכם לנהל נכון את התפריט היומי, והכי חשוב בקלות!${<br></br>} בגליון הראשון תמצאו את המאכלים, הכמויות והקלוריות ותוכלו להוסיף מאכלים כפי רצונכם על ידי לחיצה על כפתור${<br></br>} בגליון נוסף תמצאו את המעקב היומי שלכם אליו תוכלי להוסיף מאכלים שאכלתם על ידי הכפתו ר המתאים, בכל יום חדש תוכלו להעביר את ההנתונים לארכיון ${<br></br>} בגליון האחרון תמצאו גרפים המנתחים את צריכת הקלוריות שלכם על פי מספר פילוחים`} fileUrl=""/> 
}else{
if(params=="todo"){
return <ProjectContent image={todo} title="רשימת מטלות" explain="על ידי קובץ זה תוכלו לנהל את המטלות שלכם, להוסיף מטלה, להחליט כמה זמן היא אמורה לקחת ולעקוב אחר הביצוע וההספקים שלך" fileUrl=""/>
}else{
return <ProjectContent image={budget} title="ניהול תקציב" explain="בקובץ זה תוכלו לנהל את ההוצאות וההכנסות החודשיות שלכם, להוסיף נתונים וגם לפלח אותם על ידי גרפים בגליון הייעודי" fileUrl=""/>
}
}
}
export default Project
