import { createSlice } from '@reduxjs/toolkit'
import {getUsers} from '../../services/user'
import { Track } from '../../types/Track'

const trackSlice = createSlice({
    name: 'track',
    initialState: [] as Track[],
    reducers: {
        addTrack: (state: Track[], action) => {
            state.push(action.payload)
        },
        deleteTrack: (state: Track[], action) => {
            state.splice(action.payload.index, 1)
        },
        getAllTracks: (state: Track[],action)=>{
            return action.payload
        },
        updateTrack: (state: Track[],action)=>{
            let track=state.filter(value=>{
                value.id===action.payload.id
            })
        }
    }
})
// async function InitialAllUsers(){
//     userSlice.actions.getAllUsers();
// }
export const {getAllTracks,deleteTrack,addTrack}=trackSlice.actions
export default trackSlice.reducer
