import axios from '../utils/axios.util'
import { Comment} from "../types/Comment"

export const getComments = async () => {
    const response = await fetch('https://localhost:7090/api/Comment')
    // לקריאת get & delete אין body
    // const response = await fetch('https://jsonplaceholder.typicode.com/todos', { method: 'POST', body: JSON.stringify({}) })
    const comments = await response.json()
    
    return comments
}

export const getCommentsByTrackId = async (trackId:number) => {
    
    const response = await fetch('https://localhost:7090/api/Comment/ByTrackId'+trackId)
    // לקריאת get & delete אין body
    // const response = await fetch('https://jsonplaceholder.typicode.com/todos', { method: 'POST', body: JSON.stringify({}) })
    const comments = await response.json()
    
    return comments
}


export const addComment= async (comment: Omit<Comment, 'id'>) => {
    console.log(comment)
    const response = await axios.post('/Comment', comment)
    const newComment = response.data
    return newComment
}

export const updateComment = async (comment: Comment) => {
    const response = await axios.put(`/Comment/${comment.id}`, comment)
    const updatedComment = response.data
    return updatedComment
}

export const deleteComment = async (id: number) => {
    const response = await axios.delete(`/Comment/${id}`)
    return response
}
