import * as React from 'react';
import AppBar from '@mui/material/AppBar';
import Box from '@mui/material/Box';
import Toolbar from '@mui/material/Toolbar';
import IconButton from '@mui/material/IconButton';
import Typography from '@mui/material/Typography';
import Menu from '@mui/material/Menu';
import MenuIcon from '@mui/icons-material/Menu';
import Container from '@mui/material/Container';
import Avatar from '@mui/material/Avatar';
import Button from '@mui/material/Button';
import Tooltip from '@mui/material/Tooltip';
import MenuItem from '@mui/material/MenuItem';
import AdbIcon from '@mui/icons-material/Adb';
import UserProfileImage from '../components/UserProfileImage';
import img from '../images/תמונה5.png';
import { useEffect, useState } from 'react';
import { TextField } from '@mui/material';
import LoginForm from '../sections/auth/LoginForm';
import { useNavigate } from 'react-router-dom';
import { deleteUser } from '../redux/user/user.slice';
import { useDispatch, useSelector } from 'react-redux';
import { selectUser } from '../redux/user/user.selector';
// import { makeStyles } from '@mui/styles';

// const useStyles = makeStyles((theme:any) => ({
//   avatar: {
//     width: theme.spacing(7), // Adjust the size of the avatar as needed
//     height: theme.spacing(7),
//     overflow: 'hidden', // Ensure the image doesn't overflow the Avatar boundary
//     borderRadius: '50%', // Ensure the Avatar is circular
//     display: 'flex',
//     alignItems: 'center',
//     justifyContent: 'center',
//   },
//   image: {
//     width: '100%',
//     height: '100%',
//     objectFit: 'cover', // Make the image cover the entire area of the Avatar
//   },
// }));



function Header(props:{id:number|undefined}) {
  const pages = ['Home', 'Tracks'];
const settings = ['My Profile', 'My Tracks', 'Favorites', 'Logout'];
  // const classes=useStyles();
  const user=useSelector(selectUser)
  const dispatch=useDispatch()
  const [anchorElNav, setAnchorElNav] = React.useState<null | HTMLElement>(null);
  const [anchorElUser, setAnchorElUser] = React.useState<null | HTMLElement>(null);
  const [idUser, setIdUser]=useState<number|undefined>()
  const navigate=useNavigate()
  const handleOpenNavMenu = (event: React.MouseEvent<HTMLElement>) => {
    setAnchorElNav(event.currentTarget);
  };
  const handleOpenUserMenu = (event: React.MouseEvent<HTMLElement>) => {
    console.log("hello1")
    setAnchorElUser(event.currentTarget);
    console.log(event)
  };

  function handleCloseNavMenu(page:string){
    console.log(page)
    console.log("hello2")
    setAnchorElNav(null);
    if(page==="Home"){
      navigate('/')
    }
    if(page==="Tracks"){
      navigate('/findTrack')
    }
  }

  function handleCloseUserMenu(page:string) {
    console.log("hello3")
    setAnchorElUser(null);
    // console.log(page)
    if(page==="My Tracks"){
      navigate('/user/myTracks')
    }
    if(page==="Logout"){
      console.log("hi")
      localStorage.setItem('user',"")
      localStorage.clear()
      dispatch(deleteUser())
      navigate('/')
    }
    if(page==="Add Track"){
      navigate('/user/myTracks/addTrack')
    }
    if(page==='My Profile'){
      console.log("hi")
      navigate('/user/myProfile')
    }
    if(page==='Favorites'){
      console.log("hiF")
      navigate('/user/favorites')
    }
  };
  const onSignUpHandler=()=>{
    navigate('/SignIn')
  }
 const handleMenuClick=(event: React.MouseEvent<HTMLElement>)=>{
  console.log(event);
 }
useEffect(()=>{
setIdUser(props.id)
},[props.id])
  return (
    <AppBar position="static"
    sx={{zIndex:1}}>
      <Container maxWidth="xl">
        <Toolbar disableGutters>
          <Avatar src={img}  sx={{width:'100px' ,objectFit:'cover'}} variant="square"/>
           <Typography
            variant="h6"
            noWrap
            component="a"
            href="#app-bar-with-responsive-menu"
            sx={{
              mr: 2,
              display: { xs: 'none', md: 'flex' },
              fontFamily: 'monospace',
              fontWeight: 700,
              letterSpacing: '.3rem',
              color: 'inherit',
              textDecoration: 'none',
            }}
          >
            {/* //LOGO */}
          </Typography> 

          <Box sx={{ flexGrow: 1, display: { xs: 'flex', md: 'none' } }}>
            <IconButton
              size="large"
              aria-label="account of current user"
              aria-controls="menu-appbar"
              aria-haspopup="true"
              onClick={handleOpenNavMenu}
              color="inherit"
            >
              <MenuIcon />
            </IconButton>
            <Menu
              id="menu-appbar"
              anchorEl={anchorElNav}
              anchorOrigin={{
                vertical: 'bottom',
                horizontal: 'left',

              }}
              keepMounted
              transformOrigin={{
                vertical: 'top',
                horizontal: 'left',
              }}
              open={Boolean(anchorElNav)}
              onClose={handleCloseNavMenu}
              sx={{
                display: { xs: 'block', md: 'none' },
              }}
            >
              {pages.map((page) => (
                <MenuItem key={page} onClick={()=>{handleCloseNavMenu(page)}}>
                  <Typography textAlign="center"  >{page}</Typography>
                </MenuItem>
              ))}
            </Menu>
          </Box>
          {/* <Avatar src={img}/>  */}
          {/* <AdbIcon sx={{ display: { xs: 'flex', md: 'none' }, mr: 1 }} /> */}
          {/* <Typography
            variant="h5"
            noWrap
            component="a"
            href="#app-bar-with-responsive-menu"
            sx={{
              mr: 2,
              display: { xs: 'flex', md: 'none' },
              flexGrow: 1,
              fontFamily: 'monospace',
              fontWeight: 700,
              letterSpacing: '.3rem',
              color: 'inherit',
              textDecoration: 'none',
            }}
          >
            {/* LOGO 
          </Typography> */}
          <Box sx={{ flexGrow: 1, display: { xs: 'none', md: 'flex' } }}>
            {pages.map((page) => (
              <Button
                key={page}
                onClick={()=>handleCloseNavMenu(page)}
                sx={{ my: 2, color: 'white', display: 'block' }}
              >
                {page}
              </Button>
            ))}
          </Box>

          {(idUser!==undefined)?(<Box sx={{ flexGrow: 0 }}>
            <Tooltip title="Open settings">
              <IconButton onClick={handleOpenUserMenu} sx={{ p: 0 }}>
                <UserProfileImage id={idUser}/>
              </IconButton>
            </Tooltip>
            <Menu
              sx={{ mt: '45px' }}
              id="menu-appbar"
              anchorEl={anchorElUser}
              anchorOrigin={{
                vertical: 'top',
                horizontal: 'right',
              }}
              keepMounted
              transformOrigin={{
                vertical: 'top',
                horizontal: 'right',
              }}
              open={Boolean(anchorElUser)}
              onClose={()=>handleCloseUserMenu("")}
            >
              {settings.map((setting) => (
                <MenuItem key={setting} onClick={()=>handleCloseUserMenu(setting)}>
                  <Typography textAlign="center">{setting}</Typography>
                </MenuItem>
              ))}
            </Menu>
          </Box>):( <>
          <LoginForm/>
          
           <Button color="inherit" onClick={onSignUpHandler}>Sign up</Button></>)}
        </Toolbar>
      </Container>
    </AppBar>
  );
}
export default Header;
